mapper.select:
        SELECT
        

--         级联表的字段
        ,waimai.waimai_uuid_number as waimaiUuidNumber
        ,waimai.waimai_name as waimaiName
        ,waimai.waimai_photo as waimaiPhoto
        ,waimai.waimai_types as waimaiTypes
        ,waimai.waimai_old_money as waimaiOldMoney
        ,waimai.waimai_new_money as waimaiNewMoney
        ,yonghu.yonghu_name as yonghuName
        ,yonghu.yonghu_phone as yonghuPhone
        ,yonghu.yonghu_photo as yonghuPhoto
        ,yonghu.new_money as newMoney

        FROM cart  a
        left JOIN waimai waimai ON a.waimai_id = waimai.id
        left JOIN yonghu yonghu ON a.yonghu_id = yonghu.id

        
            
                and a.id in
                
                #{item}
                
            
            
                and (
                    a.yonghu_id = #{params.yonghuId}
                )
            
            
                and (
                    a.waimai_id = #{params.waimaiId}
                )
            
            
                  and a.buy_number >= #{params.buyNumberStart}   
            
            
                  and a.buy_number <= #{params.buyNumberEnd}   
            
             
                and a.buy_number = #{params.buyNumber}
             
            
                  and UNIX_TIMESTAMP(a.update_time) >= UNIX_TIMESTAMP(#{params.updateTimeStart}) 
            
            
                  and UNIX_TIMESTAMP(a.update_time) <= UNIX_TIMESTAMP(#{params.updateTimeEnd}) 
            
            
                  and UNIX_TIMESTAMP(a.insert_time) >= UNIX_TIMESTAMP(#{params.insertTimeStart}) 
            
            
                  and UNIX_TIMESTAMP(a.insert_time) <= UNIX_TIMESTAMP(#{params.insertTimeEnd}) 
            

                
            
                and a.waimai_id IS NOT NULL
            
            
                and waimai.waimai_uuid_number like CONCAT('%',#{params.waimaiUuidNumber},'%')
            
            
                and waimai.waimai_name like CONCAT('%',#{params.waimaiName},'%')
            
            
                and waimai.waimai_types = #{params.waimaiTypes}
            

            
                  and waimai.waimai_kucun_number >= #{params.waimaiKucunNumberStart}   
            
            
                  and waimai.waimai_kucun_number <= #{params.waimaiKucunNumberEnd}   
            
            
                and waimai.waimai_kucun_number = #{params.waimaiKucunNumber}
            
            
                  and waimai.waimai_price >= #{params.waimaiPriceStart}   
            
            
                  and waimai.waimai_price <= #{params.waimaiPriceEnd}   
            
            
                and waimai.waimai_price = #{params.waimaiPrice}
            
            
                  and waimai.waimai_old_money >= #{params.waimaiOldMoneyStart}   
            
            
                  and waimai.waimai_old_money <= #{params.waimaiOldMoneyEnd}   
            
            
                  and waimai.waimai_new_money >= #{params.waimaiNewMoneyStart}   
            
            
                  and waimai.waimai_new_money <= #{params.waimaiNewMoneyEnd}   
            
            
                  and waimai.waimai_clicknum >= #{params.waimaiClicknumStart}   
            
            
                  and waimai.waimai_clicknum <= #{params.waimaiClicknumEnd}   
            
            
                and waimai.waimai_clicknum = #{params.waimaiClicknum}
            
            
                and waimai.waimai_content like CONCAT('%',#{params.waimaiContent},'%')
            
            
                and waimai.shangxia_types = #{params.shangxiaTypes}
            

            
                  and waimai.waimai_delete >= #{params.waimaiDeleteStart}   
            
            
                  and waimai.waimai_delete <= #{params.waimaiDeleteEnd}   
            
            
                and waimai.waimai_delete = #{params.waimaiDelete}
            
                
            
                and a.yonghu_id IS NOT NULL
            
            
                and yonghu.yonghu_name like CONCAT('%',#{params.yonghuName},'%')
            
            
                and yonghu.yonghu_phone like CONCAT('%',#{params.yonghuPhone},'%')
            
            
                and yonghu.yonghu_id_number like CONCAT('%',#{params.yonghuIdNumber},'%')
            
            
                and yonghu.yonghu_email like CONCAT('%',#{params.yonghuEmail},'%')
            
            
                  and yonghu.new_money >= #{params.newMoneyStart}   
            
            
                  and yonghu.new_money <= #{params.newMoneyEnd}   
            
            
                  and yonghu.yonghu_sum_jifen >= #{params.yonghuSumJifenStart}   
            
            
                  and yonghu.yonghu_sum_jifen <= #{params.yonghuSumJifenEnd}   
            
            
                  and yonghu.yonghu_new_jifen >= #{params.yonghuNewJifenStart}   
            
            
                  and yonghu.yonghu_new_jifen <= #{params.yonghuNewJifenEnd}   
            
            
                and yonghu.huiyuandengji_types = #{params.huiyuandengjiTypes}
            

        

        order by a.${params.orderBy} desc
